    id: PropTypes.string,
  }

  state={
    movie:{}
  }

  _fetchMovie= ({id})=>{
    fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&i=${id}`)
    .then(res=>res.json())
    .then(movie =>{
      console.log(movie)
      this.setState({movie})
    
