import React, {Component} from 'react'

export class SearchForm extends Component{

  state={
    inputMovie: ''
  }
  render() {
    return(
      <div className="field has-addons">
        <div className="control">
          <input 
            className="input" 
            type="text" 
            onChange={this}
            placeholder="Movie to search.."/>
        </div>
        <div className="control">
          <button className="button is-info"> 
            Search
          </button>
        </div>
      </div>
    )
  }
}
